import { Grid, TextField } from "@material-ui/core";
import Typography from "@material-ui/core/Typography";
import Alert from "@material-ui/lab/Alert";
import React, { useState } from "react";
import Carousel from 'react-material-ui-carousel';
import CustomForm from "@/shared/Components/CustomForm.js";
import { handleTextBoxChange } from "@/shared/UtilityFunctions";
import { makeStyles } from "@material-ui/core/styles";
import style from "./style";

// TODO: change to props
const puzzle2 = require("../../../rooms/euphorigen/json/puzzle2.json");
const useStyles = makeStyles(style)

const Puzzle2 = () => {

  const classes = useStyles()

  const public_url = process.env.PUBLIC_URL;
  const { puzzleText, images, password } = puzzle2;

  //preloads carousel images. Not sure if it actually does anything.
  var allImages = []
  images.forEach((image) => {
    const img = new Image()
    img.src = public_url + image.src
    allImages.push(img)
  });

  const CImage = (props) => {
    const [loaded, setLoaded] = useState(false);

    return (
      <div>
        {loaded ? null : (
          <div
            style={{
              background: 'white',
              width: '492px',
              height: '700px'
            }}
          ></div>
        )}
        <img
          style={loaded ? {} : { display: 'none' }}
          src={props.value.src}
          width="600px"
          height="421px"
          alt="alt"
          onLoad={() =>  setLoaded(true)}
        />
      </div>
    );
  }

  return (
    <Grid container direction="column" alignItems="center" justifyContent="center" className={classes.background}>
      <Grid className={classes.backGroundImgContainer}>
        {images.map((value, key) => (
            <link
              rel = "prefetch"
              href={public_url + value.src}
              as="image"
              key={key}
            ></link>
            ))}

        <Grid container direction="row" alignItems="center" justifyContent="center" className={classes.alert}>
          <Alert severity="info">
             {puzzleText.message}
          </Alert>
        </Grid>

        <Grid container direction="row" alignItems="center" justifyContent="center" wrap="nowrap">
          <Grid className={classes.promptsContainer}>
            <Typography component="div" variant="body1">
              {puzzleText.title}
              <div style={{marginLeft:"10px"}}>
                {puzzleText.content.map(({prompt, bullet}, index) => (
                  <div key={index} style={{marginTop:"10px"}}>
                    <strong>{prompt}</strong>
                    <Grid container direction="row" alignItems="center" wrap="nowrap">
                      <Grid item xs={2}>
                        <TextField
                          variant="outlined"
                          label=""
                          size="small"
                          inputProps={{ maxLength: 6 }}
                          className={classes.textField}
                          onBlur={event => {
                            handleTextBoxChange(event, index)
                          }}
                      />
                      </Grid>
                      <Grid>
                        <span>{bullet}</span>
                      </Grid>
                    </Grid>
                  </div>
                ))}
              </div>
            </Typography>
          </Grid>

          <Grid className={classes.carouselContainer}>
            <Carousel autoPlay={false} navButtonsAlwaysVisible={true}>
              {allImages.map((value, key) => (
                <CImage value = {value} key={key}></CImage>
                ))}
            </Carousel>
          </Grid>
        </Grid>

        <Grid style={{position: "relative"}}>
          <div className={classes.passwordFormContainer} >
            <CustomForm id={2} password={password} />
          </div>
        </Grid>
      </Grid>
    </Grid>
  );
};

export default Puzzle2;
